// Format the AI response for the chatbot
const aiResponse = $input.first().json.choices[0].message.content;
const originalMessage = $('Chat Input Webhook').first().json.body.message;
const systemData = $('Get Current Data').first().json;

// Add metadata and formatting
const formattedResponse = {
  success: true,
  response: {
    type: 'ai_analysis',
    message: aiResponse,
    timestamp: new Date().toISOString(),
    request_id: Math.random().toString(36).substr(2, 9),
    source: 'n8n_ai_agent'
  },
  original_query: originalMessage,
  system_data_used: systemData ? 'yes' : 'no'
};

return { json: formattedResponse };

// Fallback response when data fetch fails
const originalMessage = $('Chat Input Webhook').first().json.body.message;
const errorData = $('Get Current Data').first().json;

const fallbackResponse = {
  success: true,
  response: {
    type: 'fallback',
    message: `I apologize, but I'm having trouble accessing the current dengue data right now. However, I can still provide general information about dengue prevention and monitoring.\n\nRegarding your question: "${originalMessage}"\n\nüìã **General Dengue Information:**\n‚Ä¢ Monitor symptoms: fever, headache, muscle pain\n‚Ä¢ Eliminate standing water sources\n‚Ä¢ Use mosquito repellent and nets\n‚Ä¢ Seek immediate medical attention for severe symptoms\n\nPlease try again in a moment, or contact your local health office for current statistics.`,
    timestamp: new Date().toISOString(),
    request_id: Math.random().toString(36).substr(2, 9),
    source: 'n8n_fallback'
  },
  original_query: originalMessage,
  error_info: errorData
};

return { json: fallbackResponse };
