// Process Ollama response and format for chatbot
const ollamaResponse = $input.first().json.response;
const originalMessage = $('Chat Input Webhook').first().json.body.message;
const systemData = $('Get Current Data').first().json;

// Clean up the AI response
let cleanResponse = ollamaResponse;
if (cleanResponse) {
  // Remove any system prompt echoes
  cleanResponse = cleanResponse.replace(/^(You are a specialized|Assistant:|User Question:).*?\n/gm, '');
  cleanResponse = cleanResponse.trim();
}

// Add metadata and formatting
const formattedResponse = {
  success: true,
  response: {
    type: 'ai_analysis',
    message: cleanResponse || 'I apologize, but I\'m having trouble generating a response right now. Please try asking a more specific question about dengue monitoring.',
    timestamp: new Date().toISOString(),
    request_id: Math.random().toString(36).substr(2, 9),
    source: 'ollama_free_ai',
    model: 'llama2'
  },
  original_query: originalMessage,
  system_data_used: systemData && systemData.success ? 'yes' : 'no'
};

return { json: formattedResponse };

// Fallback response when AI fails
const originalMessage = $('Chat Input Webhook').first().json.body.message;
const aiError = $('FREE AI Assistant (Ollama)').first().json;

const fallbackResponse = {
  success: true,
  response: {
    type: 'fallback',
    message: `I apologize, but the AI assistant is currently unavailable. However, I can provide general dengue information.\n\nRegarding your question: "${originalMessage}"\n\nüìã **General Dengue Guidelines:**\n‚Ä¢ **Symptoms to Monitor**: High fever (40¬∞C), severe headache, muscle pain, nausea\n‚Ä¢ **Prevention**: Eliminate standing water, use repellent, wear long sleeves\n‚Ä¢ **Seek Medical Help**: For persistent fever, bleeding, difficulty breathing\n‚Ä¢ **High-Risk Areas**: Areas with recent cases or poor sanitation\n\nüè• **For current statistics and risk levels, please contact your local health office or try again later when the AI service is restored.**`,
    timestamp: new Date().toISOString(),
    request_id: Math.random().toString(36).substr(2, 9),
    source: 'php_fallback_ai_unavailable'
  },
  original_query: originalMessage,
  ai_error: 'Ollama AI service unavailable'
};

return { json: fallbackResponse };
